mapper.select:
        SELECT 
        FROM CATEGORY
        
			
                CATEGORY_OID = #{key.categoryOid,jdbcType=DECIMAL}
            
			
				AND CATEGORY_DESC = #{categoryDesc,jdbcType=NVARCHAR}
			
			
				AND MONTHLY_BUDGET = #{monthlyBudget,jdbcType=DECIMAL}
			
			
                AND CATEGORY_LEVEL = #{categoryLevel,jdbcType=DECIMAL}
            
            
                AND IS_LEAF = #{isLeaf,jdbcType=DECIMAL}
            
			
				AND PARENT_OID = #{parentOid,jdbcType=DECIMAL}
			
			
                AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
            
            
                AND UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
            
            
                AND CREATE_BY = #{createBy,jdbcType=NVARCHAR}
            
            
                AND UPDATE_BY = #{updateBy,jdbcType=NVARCHAR}
            
            
                AND SEQ_NO = #{seqNo,jdbcType=DECIMAL}

mapper.update:
UPDATE CATEGORY SET SEQ_NO = SEQ_NO + 1, MONTHLY_BUDGET = #{monthlyBudget,jdbcType=DECIMAL},
IS_LEAF = #{isLeaf,jdbcType=DECIMAL},
UPDATE_BY = #{updateBy,jdbcType=NVARCHAR}, UPDATE_TIME = current_timestamp WHERE CATEGORY_OID = #{categoryOid,jdbcType=DECIMAL} AND SEQ_NO = #{seqNo,jdbcType=DECIMAL}
