mapper.select:
        SELECT 
        FROM CATEGORY
        
			
                CATEGORY_OID = #{key.categoryOid,jdbcType=DECIMAL}
            
			
				AND CATEGORY_DESC = #{categoryDesc,jdbcType=NVARCHAR}
			
			
				AND PARENT_OID = #{parentKey.categoryOid,jdbcType=DECIMAL}
			
			
                AND CATEGORY_LEVEL = #{categoryLevel,jdbcType=DECIMAL}

mapper.update:
UPDATE CATEGORY SET SEQ_NO = SEQ_NO + 1, MONTHLY_BUDGET = #{monthlyBudget,jdbcType=DECIMAL},
IS_LEAF = #{leaf,jdbcType=BOOLEAN},
UPDATE_BY = #{updateBy,jdbcType=NVARCHAR}, UPDATE_TIME = current_timestamp WHERE CATEGORY_OID = #{categoryOid,jdbcType=DECIMAL} AND SEQ_NO = #{seqNo,jdbcType=DECIMAL}
