mapper.select:
        SELECT 
        FROM ACCOUNT_AUDIT
        
			
				ADT_OID = #{key.adtOid,jdbcType=DECIMAL}
			
			
                AND ACNT_OID = #{acntOid,jdbcType=DECIMAL}
            
			
                AND BATCH_NUM = #{batchNum,jdbcType=DECIMAL}

mapper.delete:
DELETE FROM ACCOUNT_AUDIT

    
        ADT_OID = #{adtOid,jdbcType=DECIMAL}
    
    
        AND ACNT_OID = #{acntOid,jdbcType=DECIMAL}
    
    
        AND BATCH_NUM = #{batchNum,jdbcType=DECIMAL}
