mapper.select:
SELECT 
FROM ACCOUNT

    
        ACNT_OID = #{key.acntOid,jdbcType=DECIMAL}
    
    
        AND ACNT_TYPE = #{acntType,jdbcType=VARCHAR}
    
    
        AND OWNER_OID = #{ownerOid,jdbcType=DECIMAL}

mapper.update:
UPDATE ACCOUNT SET BALANCE = #{balance,jdbcType=DECIMAL},
DEBT = #{debt,jdbcType=DECIMAL},
SEQ_NO = SEQ_NO + 1, UPDATE_BY = #{updateBy,jdbcType=NVARCHAR}, UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP} WHERE ACNT_OID = #{acntOid,jdbcType=DECIMAL} AND SEQ_NO = #{seqNo,jdbcType=DECIMAL}
